import React, { useState } from 'react';
import {Route, Link, useHistory} from 'react-router-dom';
import { useParams } from 'react-router-dom'
import Details from '../pages/Details';

const Display = (props) => {

    let history = useHistory();

    const handleMovieDetails = (movies) => {
        history.push(
            <Link to={`/${movies}`}></Link>
        )
        }

    let moviesDisplay=(<div></div>)

    if (!props.fetching && props.movieData.length !== 0) {

        console.log(props.movieData);

        moviesDisplay = props.movieData.results.map((movies, index) => {
            return (
                <div className="card" onClick={handleMovieDetails}>
                    <div key={index}>{movies.original_title}</div>
                    <img src={`https://www.themoviedb.org/t/p/w220_and_h330_face/${movies.poster_path}`}></img>
                </div>
            )
        })

    }

        return (
            <div>
                {moviesDisplay}
            </div>
        )
}

export default Display;
