import './App.css';
import React, {useState, useEffect} from 'react'
import Search from './components/Search';
import Display from './components/Display'

function App() {

  const [movieData, setMovieData] = useState('');
  const [fetched, setFetched] = useState(true);
  const [searchInput, setSearchInput] = useState('')

  const fetchMovies = async (url) => {

        try {
            const res = await fetch(url);

            if (res.status !== 200) {
              throw new Error('Something went wrong.');
            }

            movieData = await res.json();

            console.log(movieData)

            if (searchInput.length === 0) {
                return
            }

          setMovieData(movieData);

        } catch (err) {
            console.log('error!');
        }
        console.log('done loading');   
    }

  useEffect(() => {

    const url = (`https://api.themoviedb.org/3/discover/movie?api_key=31a40805676bdd5d1f2295449e6165c1&with_keywords=${searchInput}`)

    if (fetched === false) {
            fetchMovies(url);
        }

      },[fetched])


  return (
    <>
      <Search setSearchInput={setSearchInput} setFetched={setFetched}></Search>
      <Display movieData={movieData}></Display>
    </>
  );
}

export default App;
