    console.log(searchInput)

    if (fetching && searchInput !== '') {
            fetchMovies(url);
        }

      },[fetching])


  return (
    <>
      <Search setSearchInput={setSearchInput} setFetching={setFetching}></Search>
      <Display movieData={movieData} fetching={fetching}></Display>
    </>
  );
}

export default App;
