import './App.css';
import React, { useState, useEffect } from 'react'
import {Route} from 'react-router-dom';

import Search from './components/Search';
import Display from './components/Display'
import Details from './pages/Details'


function App() {

  const [movieData, setMovieData] = useState('');
  const [popularMovies, setPopularMovies] = useState('');
  const [fetchingSearch, setFetchingSearch] = useState(false);
  const [searchInput, setSearchInput] = useState('')



  const fetchPopular = async (url) => {
    try {
      const res = await fetch(url);
      if (res.status !== 200) {
        throw new Error('something went wrong');
      }

      setPopularMovies(await res.json());
      console.log(popularMovies);

    } catch (err) {
        console.log(err);
    }

  }

  useEffect(() => {
    const url = 'https://api.themoviedb.org/3/movie/popular?api_key=31a40805676bdd5d1f2295449e6165c1&language=en-US&page=1'

    if (searchInput === '') {
      fetchPopular(url);
    }

    return (() => {
      console.log('component unmounted');
    })

  }, [])


  const fetchMovies = async (url) => {

    try {
      const res = await fetch(url);
      console.log(res)
      if (res.status !== 200) {
        throw new Error('Something went wrong.');
      }

      setMovieData(await res.json());    
    } catch (err) {
        console.log(err);
    }

    setFetchingSearch(false);

    console.log('done loading');   
    }


  useEffect(() => {

    const url = (`https://api.themoviedb.org/3/search/movie?language=en-US&include_adult=false&page=1&api_key=31a40805676bdd5d1f2295449e6165c1&query=${searchInput}`)


    if (fetchingSearch && searchInput !== '') {
            fetchMovies(url);
      }

      },[fetchingSearch])



  let popular = popularMovies.results.map((movies, index) => {

        return (
            <div className="popular">
                <div key={index}>{movies.original_title}</div>
                <img src={`https://www.themoviedb.org/t/p/w220_and_h330_face/${movies.poster_path}`} alt=''></img>
            </div>
            )
        })


  return (
    <>
      <Route exact path='/'>

        <Search setSearchInput={setSearchInput} setFetchingSearch={setFetchingSearch}></Search>
        {popular};
        <Display movieData={movieData} fetching={fetchingSearch} popularMovies={popularMovies} searchInput={searchInput}></Display>
      </Route>
      <Route exact path='/:movies'><Details/></Route>
    </>
  );
}

export default App;
